import anotherFunction from './anotherFunction';

function createChart() {

    const sleep = ms => new Promise(resolve => setTimeout(resolve, ms));
  
    async function sleepyWork() {
      console.log("I'm going to sleep for 1 second.");
      await sleep(1000);
      console.log('I woke up after 1 second.');
      const pet = document.getElementById('pet-select').value;
      console.log("Pet selected: " + pet);
      data[0] = 4;
      chart.update();
    }
  
    const ctx = document.createElement('canvas');
    ctx.id = 'myChart';
  
    const data = [20, 19, 3, 5, 2, 3];
  
    chart = new Chart(ctx, {
      type: 'radar',
      data: {
        labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
        datasets: [{
          label: '# of Votes',
          data: data,
          borderWidth: 1
        }]
      },
      options: {
        responsive: true,
        plugins: {
          title: {
            display: true,
            text: 'Chart.js Radar Chart'
          }
        }
      }
    });
  
    const select = document.createElement('select');
    select.id = 'pet-select';
    select.onchange = sleepyWork;
  
    const option1 = document.createElement('option');
    option1.value = '';
    option1.textContent = '--Please choose an option--';
  
    const option2 = document.createElement('option');
    option2.value = 'dog';
    option2.textContent = 'Dog';
  
    const option3 = document.createElement('option');
    option3.value = 'cat';
    option3.textContent = 'Cat';
  
    const option4 = document.createElement('option');
    option4.value = 'hamster';
    option4.textContent = 'Hamster';
  
    const option5 = document.createElement('option');
    option5.value = 'parrot';
    option5.textContent = 'Parrot';
  
    const option6 = document.createElement('option');
    option6.value = 'spider';
    option6.textContent = 'Spider';
  
    const option7 = document.createElement('option');
    option7.value = 'goldfish';
    option7.textContent = 'Goldfish';
  
    select.appendChild(option1);
    select.appendChild(option2);
    select.appendChild(option3);
    select.appendChild(option4);
    select.appendChild(option5);
    select.appendChild(option6);
    select.appendChild(option7);
  
    const div = document.createElement('div');
    div.appendChild(ctx);
  
    const body = document.createElement('body');
    body.appendChild(select);
    body.appendChild(div);
  
    const html = document.createElement('html');
    html.appendChild(body);
  
    const result = html.outerHTML;
    return result;
  }
  
  export default createChart;
