import '../App.css';
import React, {useEffect, useState} from 'react';
import SelectComponent from './SelectComponent';
// import {Link} from 'react-router-dom';

function Tweet() {


   /*  const options = [ 
        {key: 1, value: "Test 1"},
        {key: 2, value: "Test 2"},
        {key: 3, value: "Test 3"}
    ]; */

    const [selectedOption, setSelectedOption] = useState("");
    //const [selectedTopic, setSelectedTopic] = useState(""); //new
    const [items, setItems] = useState([]);


    useEffect( () => {
        fetchItems();
    }, []);

    

    const fetchItems = async () => {
        const data = await fetch('/courses');
        const items = await data.json();
        setItems(items);
    };

    const options = items.map(item => {
        return {
            key: item._id,
            value: item.CourseID
            //topic: item.topic_1, //new
            //probability: item.probability_1 //new
        }
    });

    //console.log("HELLO")

   /*  {
        items.map(item => (
            <div className="courses-map">
                <p>Course name: {item.CourseID}</p>
                <p>Course id: {item.courseName}</p>
                <p>Topic/Probability: {item.topic}</p>
            </div>
        ))
        } */

/*         <section>
            <div> 
                <SelectComponent
                    options = {options}
                    onChange = {(item) => setSelectedOption(item)}
                    selectedKey={selectedOption}
                    placeholder={"type to search"}
                />
                <p className="sel-option"> Selected option: {selectedOption}</p>
            </div>
            
            test
            {fetchItems.items}
            
            <div className="courses-map">
                <p>Course name: {options.CourseID}</p>
                <p>Course id: {options.courseName}</p>
                <p>Topic/Probability: {options.topic}</p>
            </div>
    
            
        </section>   */   
        
    
        const sleep = ms => new Promise(resolve => setTimeout(resolve, ms));

        async function sleepyWork() {
        console.log("I'm going to sleep for 1 second.");
        await sleep(1000);
        console.log('I woke up after 1 second.');
        pet = document.getElementById('pet-select').value;
        console.log("Pet selected: " + pet);
        data[0] = 4;
        chart.update();
        }

        const ctx = document.getElementById('myChart');

        const data = [20, 19, 3, 5, 2, 3];

        //sleepyWork(); // Test

        chart = new Chart(ctx, {
            type: 'radar',
            data: {
            labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
            datasets: [{
                label: '# of Votes',
                data: data,
                borderWidth: 1
                }]
                },
            options: {
            responsive: true,
            plugins: {
                title: {
                display: true,
                text: 'Chart.js Radar Chart'
                }
            }
            }
            });
          
    return(
        <section>
            <head>



            <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>


            </head>



            <body>

            MA SUPER PAGE WEB

            <select id="pet-select" onchange="sleepyWork()">
                <option value="">--Please choose an option--</option>
                <option value="dog">Dog</option>
                <option value="cat">Cat</option>
                <option value="hamster">Hamster</option>
                <option value="parrot">Parrot</option>
                <option value="spider">Spider</option>
                <option value="goldfish">Goldfish</option>
            </select>

            <div>
            <canvas id="myChart"></canvas>
            </div>

            </body>
        </section>
    );
}



export default Tweet;
